import React from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useEffect, useState } from 'react';

const Chamcong = () => {

    const[Nhansus, setNhansu] = useState([]);
    const[Chucvus, setChucvu] = useState([])
    useEffect(()=> {
        axios.get('http://localhost:3000/nhansu')
        .then((res) => setNhansu(res.data))
        .catch(err => console.log(err));

        axios.get('http://localhost:3000/chucvu')
        .then(Response => {
            if(Response.data) {
                setChucvu(Response.data);
            } else {
                alert(Response.data)
            }
        })
        .catch(err => console.log(err));
    },)

   // const navigate = useNavigate()
    return ( 
        <div>
            <Link to="/quanlychamcong/quanlycalamviec/xaydungcalamviec/themcalamviec" className='btn btn-success'>
                Chấm Công
            </Link>
            <table className='table'>
            <thead>
                    <tr>
                        <th>Mã Nhân Viên</th>
                        <th>Tên</th>
                        <th>Chức Vụ</th>
                        <th>Ngày Chấm Công</th>
                        <th>Tùy Chọn</th>
                    </tr>
                </thead>
                <tbody>
                    {
                       Nhansus.map(e => {
                        <div id="overlay">
                        <div id="centeredTable">
                            <p>Dòng chữ trong bảng</p>
                            <table>
                            <button onclick="tacBang()">tắc bảng</button>
                            <tr>
                                <th>Header 1</th>
                                <th>Header 2</th>
                            </tr>
                            <tr>
                                <td>Data 1</td>
                                <td>Data 2</td>
                            </tr>
                            </table>
                        </div>
                        </div>
                        const correspondingChucvu = Chucvus.find((cv) => cv._id === e.Chucvu);
                        return <tr>
                            <td>{e.Mnv}</td>
                            <td>{e.Hoten}</td>
                            <td>{correspondingChucvu ? correspondingChucvu.Tenchucvu : '-'}</td>
                            <td><input type='date' required></input></td>

                            <td>
                                <Link to={'/quanlythongtinnhanvien/Chinhsua/&idns='+ e._id} className='btn btn-info btn-sm me-2'> 
                                    Chấm Công</Link>
                                <button className='btn btn-warning btn-sm' >
                                    Chấm Lại</button>
                            </td>
                        </tr>
                       })
                    }
                </tbody>
            </table>
        </div>
     );
}
 
export default Chamcong;
