import React from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useEffect, useState } from 'react';
import '../../../../viewcss/qlchamcong/qlcalamviec/Cc.css';

const Chamcong = () => {

    const[Nhansus, setNhansu] = useState([]);
    const[Chucvus, setChucvu] = useState([])
    useEffect(()=> {
        axios.get('http://localhost:3000/nhansu')
        .then((res) => setNhansu(res.data))
        .catch(err => console.log(err));

        axios.get('http://localhost:3000/chucvu')
        .then(Response => {
            if(Response.data) {
                setChucvu(Response.data);
            } else {
                alert(Response.data)
            }
        })
        .catch(err => console.log(err));
    },)
    const handleDelete = (id) =>{
        axios.delete("http://localhost:3000/calamviec/"+ id)
        .then(res => {
            if(res.data){
                navigate('/quanlythongtinnhanvien')
            } else (
                alert(res.data)
        )})
    }
    function hienthibang() {
        var overlay = document.getElementById("overlay");
        overlay.style.display = "block";
    }
    function tacbang() {
        var overlay = document.getElementById("overlay");
        overlay.style.display = "none";
    }
    const [isOverlayVisible, setIsOverlayVisible] = useState(false);

    function hienthibang() {
        setIsOverlayVisible(true);
    }

    function tacbang() {
        setIsOverlayVisible(false);
    }
   // const navigate = useNavigate()
    return ( 
        <div>
            <Link to="/quanlychamcong/quanlycalamviec/xaydungcalamviec/themcalamviec" className='btn btn-success'>
                Chấm Công
            </Link>
            <div>
                <div className={isOverlayVisible ? "overlay show" : "overlay"}>
                    <div className="centeredTable">
                        <p>Dòng chữ trong bảng</p>
                        <table>
                            <button onclick={tacbang}>tắc bảng</button>
                            <tr>
                                <th>Header 1</th>
                                <th>Header 2</th>
                            </tr>
                            <tr>
                                <td>Data 1</td>
                                <td>Data 2</td>
                            </tr>
                        </table>
                    </div>
                </div>
            </div> 
            <table className='table'>
            <thead>
                    <tr>
                        <th>Mã Nhân Viên</th>
                        <th>Tên</th>
                        <th>Chức Vụ</th>
                        <th>Ngày Chấm Công</th>
                        <th>Tùy Chọn</th>
                    </tr>
                </thead>
                <tbody>
                    {
                       Nhansus.map(e => {
                        
                        const correspondingChucvu = Chucvus.find((cv) => cv._id === e.Chucvu);
                        return <tr>
                            <td>{e.Mnv}</td>
                            <td>{e.Hoten}</td>
                            <td>{correspondingChucvu ? correspondingChucvu.Tenchucvu : '-'}</td>
                            <td><input type='date' required></input></td>

                            <td>
                                <Link to={'/quanlythongtinnhanvien/Chinhsua/&idns='+ e._id} className='btn btn-info btn-sm me-2'> 
                                    Chấm Công</Link>
                                <button className='btn btn-warning btn-sm' onclick={hienthibang}>
                                    Chấm Lại</button>
                            </td>
                        </tr>
                       })
                    }
                </tbody>
            </table>
        </div>
     );
}
 
export default Chamcong;
