import React,{ useEffect, useState } from "react";
import axios from "axios";
import {Link, useNavigate } from 'react-router-dom'
const Qlvitri = () => {
    const navigate = useNavigate()
    const [Chucvu, setChucvu] = useState([])
 
    useEffect(() =>{
        axios.get('http://localhost:3000/chucvu')
        .then(Response => {
            if(Response.data) {
                setChucvu(Response.data);
            } else {
                alert(Response.data)
            }
        })
        .catch(err => console.log(err))
    }, []);

    const handleDelete = (id) => {
    axios.delete("http://localhost:3000/chucvu/" + id)
        .then(Response => {
            if (Response.data) {
                // Update state after successful deletion
                setChucvu(Chucvu.filter(cv => cv._id !== id));
                navigate('/quanlycongtacnhanvien/quanlyvitri');
            } else {
                alert("Delete operation failed");
            }
        })
        .catch(err => console.log(err));
    };

    return ( 
        <div className="px-5 mt-5">
            <div className="d-flex justify-content-center">
                <h3>Chuc vu</h3>
            </div>
            <Link to="/quanlycongtacnhanvien/ThemChucvu" className="btn btn-success">Them chuc vu</Link>
            <div className="mt-3">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Chuc vu</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            Chucvu.map(cv => {
                                return <tr>
                                    <td>{cv.Tenchucvu}</td>   
                                    <td><button className='btn btn-warning btn-sm' onClick={() => handleDelete(cv._id)}>Xoa</button></td>  
                                    <td><Link to={'/quanlythongtinnhanvien/Chinhsuachucvu/&idcv='+ cv._id} className='btn btn-info btn-sm me-2'>Chinh sua </Link></td>                             
                                </tr>
                            })
                        }
                    </tbody>
                </table>
            </div>
        </div>
            
     );
}
 
export default Qlvitri;
