import React,{ useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Qlvitri = () => {
    const [Chucvu, setChucvu] = useState([])
 
    useEffect(() =>{
        axios.get('http://localhost:3000/chucvu')
        .then(Response => {
            if(Response.data) {
                setChucvu(Response.data);
            } else {
                alert(Response.data)
            }
        })
        .catch(err => console.log(err))
    }, [])


    
    return ( 
        <div className="px-5 mt-5">
            <div className="d-flex justify-content-center">
                <h3>Chuc vu</h3>
            </div>
            <Link to="/quanlycongtacnhanvien/ThemChucvu" className="btn btn-success">Them chuc vu</Link>
            <div className="mt-3">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Chuc vu</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            Chucvu.map(cv => {
                                return <tr>
                                    <td>{cv.Tenchucvu}</td>   
                                    <button className='btn btn-warning btn-sm' onClick={() => handleDelete(e._id)}>
                                    Xoa</button>                               
                                </tr>
                                
                            })
                        }
                    </tbody>
                </table>
            </div>
        </div>
            
     );
}
 
export default Qlvitri;
