import axios from 'axios';
import React, { useEffect } from 'react'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom';
// ... (imports remain the same)

const ThemNhanvien = () => {
    const [Nhansu, setNhansu] = useState({
      Hoten: '',
      Cccd: '',
      Mnv: '',
      Sdt: '',
      luong: '',
      Chucvu: ''
    });
  
    const [Chucvu, setChucvu] = useState([]);
    
    useEffect(() => {
      axios.get('http://localhost:3000/chucvu')
        .then((Response) => {
          if (Response.data) {
            setChucvu(Response.data);
          } else {
            alert(Response.data);
          }
        })
        .catch((err) => console.log(err));
    }, []);
  
    const navigate = useNavigate();
  
    const handleSubmit = (event) => {
      event.preventDefault();
      const dataToSend = {
        Hoten: Nhansu.Hoten,
        Cccd: Nhansu.Cccd,
        Mnv: Nhansu.Cccd,
        Sdt: Nhansu.Cccd,
        luong: Nhansu.luong,
        Chucvu: Nhansu.Chucvu
      };
  
      axios.post('http://localhost:3000/nhansu', dataToSend)
        .then((_res) => {
          navigate('/quanlythongtinnhanvien');
        })
        .catch((err) => console.log(err));
    };
  
    return (
      <div className='d-flex justify-content-center align-items-center mt-3'>
        <div className=''>
          <h3 className=''>Them Nhanvien</h3>
          <form className='' onSubmit={handleSubmit}>
            {/* Render select only when Chucvu has data */}
            {Chucvu.length > 0 && (
              <div className=''>
                <label htmlFor='Chucvu'>Chuc vu:</label>
                <select
                  name='Chucvu'
                  id='Chucvu'
                  className=''
                  onChange={(e) => setNhansu({ ...Nhansu, Chucvu: e.target.value })}
                >
                  {Chucvu.map((cv) => (
                    <option key={cv._id} value={cv._id}>{cv.Tenchucvu}</option>
                  ))}
                </select>
              </div>
            )}
            {/* Rest of your form */}
            {/* ... */}
            <button>Them Nhan vien</button>
          </form>
        </div>
      </div>
    );
  };
  
  export default ThemNhanvien;
