import { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../viewcss/qlhoso/qlthongtinnhanvien/Qlthongtinnhanvien.css'
import { Link, NavLink, useNavigate } from 'react-router-dom';
const Qlthongtinnhanvien= () =>{
    const[Nhansus, setNhansu] = useState([])
    useEffect(()=> {
        axios.get('http://localhost:3001/getNhansu')
        .then((response) => setNhansu(response.data))
        .catch(err => console.log(err))
    },)
    const navigate = useNavigate()
    const handleDelete = (id) =>{
        axios.delete('http://localhost:3001/XoaNhanvien'+id)
        .then(result => {
            if(result.data.Status){
            navigate('/quanlythongtinnhanvien')
            } else (
            alert(result.data.Error)
        )})
    }
    return ( 
        <div>
            <Link to="/ThemNhanvien" className='btn btn-success'>
                Thêm nhân viên
            </Link>
            <table className='table'>
                <thead>
                    <tr>
                        <th>
                            Hoten
                        </th>
                        <th>
                            Cccd
                        </th>
                        <th>
                            Mnv
                        </th>
                        <th>
                            Sdt
                        </th>
                        <th>
                            luong
                        </th>
                        <th>
                            Chucvu
                        </th>
                        <th>
                            Chucvu
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {
                       Nhansus.map(e => {
                        return <tr>
                            <td>{e.Hoten}</td>
                            <td>{e.Cccd}</td>
                            <td>{e.Mnv}</td>
                            <td>{e.Sdt}</td>
                            <td>{e.luong}</td>
                            <td>{e.Chucvu}</td>
                            <td>
                                <Link to={'/quanlythongtinnhanvien/Chinhsua'+e.id} className='btn btn-info btn-sm me-2'>Chinh sua</Link>
                                <button className='btn btn-warning btn-sm'
                                onClick={() => handleDelete(e.id)}
                                >Xoa</button>
                            </td>
                        </tr>
                       })
                    }
                </tbody>
            </table>
        </div>
     );
}
 
export default Qlthongtinnhanvien;
