import axios from 'axios';
import React, { useEffect } from 'react'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom';
const ThemNhanvien = () => {
    const [Nhansu, setNhansu] = useState({
        Hoten:'',
        Cccd:'',
        Mnv:'',
        Sdt:'',
        luong:'',
        Chucvu:''

    })
    const navigate = useNavigate()
    const handleSubmit = (event) => {
		event.preventDefault();
		const formdata = new FormData();
		formdata.append("Hoten", Nhansu.Hoten);
		formdata.append("Cccd", Nhansu.Cccd);
		formdata.append("Mnv", Nhansu.Mnv);
		formdata.append("Sdt", Nhansu.Sdt);
		formdata.append("luong", Nhansu.luong);
		axios.post('http://localhost:3001/ThemNhanvien', formdata)
		.then(res => {
			navigate('/quanlythongtinnhanvien')
		})
		.catch(err => console.log(err));
	}

    const [Chucvu, setChucvu] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3000/quanlycongtacnhanvien/quanlyvitri')
        .then((result) => {
            if (result.data.Status){
                setChucvu(result.data.Result);
            } else {
                alert(result.data.Error);
            }
        })
        .catch((err) => console.log(err))
    }, []);
  return (
    <div className='d-flex justify-content-center align-items-center mt-3'>
        <div className='p-3 rounded w-50 border'>
            <h3 className='text-center'>Them Nhanvien</h3>
            <form className='row g-1' onSubmit={handleSubmit}>
                <div className='col-12'>
                    <label for="inputTen" className="form-label" htmlFor="Hoten">Ho Ten:</label>
                <input type="text"               
                id='inputTen' 
                placeholder='Nhap Hoten'
                className='form-control rounded-0'
                onChange ={(e) => setNhansu({...Nhansu, Hoten: e.target.value})}
                />
                </div>
                <div className='col-12'>
                    <label for="inputCCCD" className="form-label" htmlFor="CCCD">CCCD:</label>
                <input type="text"               
                id='inputCCCD' 
                placeholder='Nhap CCCD'
                className='form-control rounded-0'
                onChange ={(e) => setNhansu({...Nhansu, Cccd: e.target.value})}
                />
                </div>
                <div className='col-12'>
                    <label for="inputID" className="form-label" htmlFor="Mnv">Ma nhan vien:</label>
                <input type="text"               
                id='inputID' 
                placeholder='Nhap Mnv'
                className='form-control rounded-0'
                onChange ={(e) => setNhansu({...Nhansu, Mnv: e.target.value})}
                />
                </div>
                <div className='col-12'>
                    <label for="inputSdt" className="form-label" htmlFor="SDT">SDT:</label>
                <input type="text"               
                id='inputSdt' 
                placeholder='Nhap SDT'
                className='form-control rounded-0'
                onChange ={(e) => setNhansu({...Nhansu, Sdt: e.target.value})}
                />
                </div>
                
                <label for="inputLuong" className="form-label">Luong:</label>
                <input type="text"               
                id='inputLuong' 
                placeholder='Nhap Luong'
                className='form-control rounded-0'
                autoComplete='off'
                onChange ={(e) => setNhansu({...Nhansu, luong: e.target.value})}
                /> 
                <div className='col-12'>
                    <label for="inputChucvu" className="form-label" htmlFor="Chucvu">Chuc vu:</label>
                    <select name="Chucvu" id="Chucvu" className='form-select'
                    onChange ={(e) => setNhansu({...Nhansu, Chucvu: e.target.value})}>
                        {Chucvu.map((c) => {
                            return <option value={c.id}>{c.Hoten}</option>
                        })}
                    </select>
                </div>
                <button className='btn btn-success w100 rounded-0 mb-2'>Them Nhan vien</button>
            </form>
        </div>
    </div>
    
  )
}

export default ThemNhanvien
