import axios from 'axios'
import React,{ useState } from 'react'
import { useNavigate } from 'react-router-dom'
const ThemChucvu = () => {
    const[Chucvu,setChucvu]=useState()
    const navigate = useNavigate()
    const handleSubmit = (e) =>{
        e.preventDefault()
        axios.post('http://localhost:3000/quanlycongtacnhanvien/ThemChucvu', {Chucvu})
        .then(result => {
            if(result.data.Status){
                navigate('/quanlycongtacnhanvien/quanlyvitri')
            } else{
                alert(result.data.Error)
            }
        }
            )
        .catch(err => console.log(err))
    }
  return (
    <div className='d-flex justify-cotent-center align-items-center h-75'>
        <div className='p-3 rounded w-25 border'>
            <h2>Them Chuc vu</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="Chucvu"><strong>Chuc Vu</strong></label>
                    <input type="text" name='Chucvu' placeholder='Nhap Chucvu' onChange={(e)=> setChucvu(e.target.value)} className='form-control round-0'/>
                </div>
                <button className='btn btn-success w-100 rounded-0 mb-2'>Them Chucvu</button>
            </form>
        </div>
    </div>
  )
}

export default ThemChucvu
