import React from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { useState } from 'react'
import { useEffect } from 'react'
import axios from 'axios'
const Chinhsua = () => {
    const {id} = useParams()
    const [Nhansu, setNhansu] = useState({
        Hoten:'',
        Cccd:'',
        Mnv:'',
        Sdt:'',
        luong:'',
        Chucvu:''
    })
    const navigate = useNavigate()
    const handleSubmit = (event) => {
		event.preventDefault();
		const dataToSend = {
            Hoten: Nhansu.Hoten,
            Cccd: Nhansu.Cccd,
            Mnv: Nhansu.Cccd,
            Sdt: Nhansu.Cccd,
            luong: Nhansu.luong
        };
		axios.put('http://localhost:3000/nhansu/'+ id, dataToSend)
		.then(res => {
			navigate('/quanlythongtinnhanvien')
		})
		.catch(err => console.log(err));
	}
    const [Chucvu, setChucvu] = useState([])

    useEffect(() =>{
        axios.get('http://localhost:3000/nhansu')
        .then((res) => setNhansu(res.data))
        .catch(err => console.log(err))
        /*.then(result => {
            if(result.data.Status) {
                setChucvu(result.data.Result);
            } else {
                alert(result.data.Error)
            }
        })*/
        .catch(err => console.log(err))
        
        axios.get('http://localhost:3001/nhansu/'+id)
        .then(result => {
            setNhansu({
                ...Nhansu,
                Hoten: result.data.Result[0].Hoten,
                Cccd: result.data.Result[0].Cccd,
                Mnv: result.data.Result[0].Mnv,
                luong: result.data.Result[0].luong,
                Chucvu: result.data.Result[0].Chucvu,
            })
        }).catch(err => console.log(err))
    }, [])
  return (
    <div className='d-flex justify-content-center align-items-center mt-3'>
        <div className='p-3 rounded w-50 border'>
            <h3 className='text-center'>Chinh sua Nhanvien</h3>
            <form className='row g-1' onSubmit={handleSubmit}>
                <div className='col-12'>
                    <label for="inputTen" className="form-label" htmlFor="Hoten">Ho Ten:</label>
                <input type="text"               
                id='inputTen' 
                placeholder='Nhap Hoten'
                className='form-control rounded-0'
                value={Nhansu.Hoten}
                onChange ={(e) => setNhansu({...Nhansu, Hoten: e.target.value})}
                />
                </div>
                <div className='col-12'>
                    <label for="inputCCCD" className="form-label" htmlFor="CCCD">CCCD:</label>
                <input type="text"               
                id='inputCCCD' 
                placeholder='Nhap CCCD'
                className='form-control rounded-0'
                value={Nhansu.Cccd}
                onChange ={(e) => setNhansu({...Nhansu, Cccd: e.target.value})}
                />
                </div>
                <div className='col-12'>
                    <label for="inputID" className="form-label" htmlFor="Mnv">Ma nhan vien:</label>
                <input type="text"               
                id='inputID' 
                placeholder='Nhap Mnv'
                className='form-control rounded-0'
                value={Nhansu.Mnv}
                onChange ={(e) => setNhansu({...Nhansu, Mnv: e.target.value})}
                />
                </div>
                <div className='col-12'>
                    <label for="inputSdt" className="" htmlFor="SDT">SDT:</label>
                <input type="text"               
                id='inputSdt' 
                placeholder='Nhap SDT'
                className='form-control rounded-0'
                value={Nhansu.Sdt}
                onChange ={(e) => setNhansu({...Nhansu, Sdt: e.target.value})}
                />
                </div>
                
                <label for="inputLuong" className="">Luong:</label>
                <input type="text"               
                id='inputLuong' 
                placeholder='Nhap Luong'
                className=''
                autoComplete='off'
                value={Nhansu.luong}
                onChange ={(e) => setNhansu({...Nhansu, luong: e.target.value})}
                /> 
                <div className='col-12'>
                    <label for="inputChucvu" className="" htmlFor="Chucvu">Chuc vu:</label>
                    <select name="Chucvu" id="Chucvu" className=''
                    onChange ={(e) => setNhansu({...Nhansu, Chucvu: e.target.value})}>
                        {Chucvu.map((c) => {
                            return <option value={c.id}>{c.Chucvu}</option>
                        })}
                    </select>
                </div>
                <button className=''>Them Nhan vien</button>
            </form>
        </div>
    </div>
  )
}

export default Chinhsua
