import React from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useEffect, useState } from 'react';

const Xdcalamviec = () => {
    const[Calams, setCalam] = useState([])
    useEffect(()=> {
        axios.get('http://localhost:3000/calamviec')
        .then((res) => setCalam(res.data))
        .catch(err => console.log(err))
    },[])
    const handleDelete = (id) =>{
        axios.delete("http://localhost:3000/nhansu/"+ id)
        .then(res => {
            if(res.data){
                navigate('/quanlythongtinnhanvien')
            } else (
                alert(res.data)
        )})
    }
    return ( 
        <div>
            <Link to="/ThemNhanvien" className='btn btn-success'>
                Thêm nhân viên
            </Link>
            <table className='table'>
                <thead>
                    <tr>
                        <th>
                            Tencalam
                        </th>
                        <th>
                            Starttime
                        </th>
                        <th>
                            Endtime
                        </th>
                        <th>
                            DAY
                        </th>
                        <th>
                            Tùy Chọn
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {
                        Calams.map(e => {
                        return <tr>
                            <td>{e.Tencalam}</td>
                            <td>{e.Starttime}</td>
                            <td>{e.Endtime}</td>
                            <td>{e.ngày}</td>
                            <td>
                                <Link to={'/quanlythongtinnhanvien/Chinhsua/&idns='+ e._id} className='btn btn-info btn-sm me-2'>
                                    Chinh sua </Link>
                                <button className='btn btn-warning btn-sm' onClick={() => handleDelete(e._id)}>
                                    Xoa</button>
                            </td>
                        </tr>
                       })
                    }
                </tbody>
            </table>
        </div>
     );
}

export default Xdcalamviec;
